import React from 'react';

let current_time = new Date();
 current_time  = current_time.getHours();
 let greeting = '';
 const cssStyle = {};

 if(current_time>=1 && current_time<12)
 {
     greeting = 'Good morning';
     cssStyle.color = "green";

 }
 else if(current_time>=12 && current_time<20)
 {
     greeting = 'Good afternoon';
     cssStyle.color = "Orange";

 }
 else if(current_time>=20 && current_time<1)
 {
     greeting = 'Good night';
     cssStyle.color = "black";

 }


function Greeting(){
    return(

        <h1>
        <div className="ayush">

        Hello sir, <span style={cssStyle}> {greeting}</span>  


        </div>
         
        </h1>
    );
}
export default Greeting;
